import { useState } from 'react';

const LoginBox = props => {

    const [list, setList] = useState([]);
    const [name, setName] = useState(null);
    const [age, setAge] = useState(null);

const submitHandler = () => {
    let user = {name: name, age: age};
    console.log(user)
setList(prevValue => [...prevValue, user]);

}

const nameHandler = (e) => {
    if (e.target.value.length === 0) {
        console.log("ERROR NO NAME ENETERED")
    }
setName(e.target.value)
console.log(name)
}

const ageHandler = (e) => {
    setAge(e.target.value)
    console.log(age)
}


    return (
<div className="box">

<form onSubmit={submitHandler}>
  <label>First name:</label>
  <input type="text" onChange={(e) => nameHandler(e)}/><br/><br/>

  <label>Age (Years):</label>
  <input type="text" onChange={(e) => ageHandler(e)}/><br/><br/>

  <button type="submit" value="Submit">Add User</button>
</form>

</div>



    )
}

export default LoginBox
